import cn.smbms.pojo.Goods;import cn.smbms.service.GoodsService;import org.junit.Test;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;import java.util.HashMap;import java.util.Map;public class test {    @Test    public void test1() {        ApplicationContext context = new ClassPathXmlApplicationContext("applicationContext-dao.xml", "applicationContext-service.xml");        GoodsService goodsService = (GoodsService) context.getBean("goodsService");        Map<String, Object> param = new HashMap<>();        param.put("goods_district", 4);        for (Goods goods : goodsService.getGoods(param)) {            System.out.println(goods.getGoods_name());        }    }    @Test    public void test2() {        ApplicationContext context = new ClassPathXmlApplicationContext("applicationContext-dao.xml", "applicationContext-service.xml");        GoodsService goodsService = (GoodsService) context.getBean("goodsService");        Goods goods = new Goods();        goods.setId(1);        goods.setGoods_name("苹果");        System.out.println(goodsService.update(goods));    }}